<?phpdefined('SYSPATH') or die('No direct script access.');class Helper_Participante {    public static function isLogged() {        return (Session::instance()->get('participante_id') != null);    }    public static function getParticipante() {        $participante_id = Session::instance()->get('participante_id');        $participante = ORM::factory('Participante', $participante_id);        return $participante;    }    public static function atualizaUltimoPerfilVisitado($perfil_id) {        $participante = self::getParticipante();        $participante->ultimoperfilvisitado_id = $perfil_id;        $participante->save();    }}?>