<?phpclass Model_Cidade extends ORM {    public $_table_name = 'cidades';      protected $_belongs_to = array(        'estado' => array('model' => 'Estado', 'foreign_key' => 'estado_id'),    );        public function rules() {        return array(            'nome' => array(                array('not_empty'),                array('min_length', array(':value', 3))            ),            'estado_id' => array(                array('not_empty'),            ),        );    }}