<?phpclass Model_Estado extends ORM {    public $_table_name = 'estados';    protected $_has_many = array(        'cidades' => array(            'model' => 'Cidade'),    );    public function rules() {        return array(            'nome' => array(                array('not_empty'),                array('min_length', array(':value', 3))            ),            'sigla' => array(                array('not_empty'),                array('min_length', array(':value', 2)),                array('max_length', array(':value', 2))            ),        );    }}