<?phpclass Model_Participante extends ORM {    public $_table_name = 'participantes';    protected $_belongs_to = array(        'cidade' => array('model' => 'Cidade', 'foreign_key' => 'cidade_id'),        'ultimoperfil' => array('model' => 'Participante', 'foreign_key' => 'ultimoperfilvisitado_id'),    );    public function rules() {        return array(            'login' => array(                array('not_empty'),                array('min_length', array(':value', 3))            ),            'cidade_id' => array(                array('not_empty'),            ),            'email' => array(                array('not_empty'),                array('email'),            ),            'foto' => array(                array('not_empty'),            ),        );    }}