<?phpdefined('SYSPATH') or die('No direct script access.');class ORM extends Kohana_ORM {    public function __get($column) {        $retorno = parent::__get($column);        if ($retorno != null) {            $tablecolumns = parent::table_columns();            if (isset($tablecolumns[$column]) && $tablecolumns[$column]['data_type'] == 'date') {                $retorno = Helper_Datahora::getDate($retorno, Helper_Datahora::DATE_BR);            } else if (isset($tablecolumns[$column]) && $tablecolumns[$column]['data_type'] == 'datetime') {                $retorno = Helper_Datahora::getDate($retorno, Helper_Datahora::DATETIME_BR);            }        }        return $retorno;    }    public function __set($column, $value) {        $tablecolumns = parent::table_columns();        if (isset($tablecolumns[$column]) && $tablecolumns[$column]['data_type'] == 'date') {            $value = Helper_Datahora::getDate($value, Helper_Datahora::DATE_EN);        } else if (isset($tablecolumns[$column]) && $tablecolumns[$column]['data_type'] == 'datetime') {            $value = Helper_Datahora::getDate($value, Helper_Datahora::DATETIME_EN);        }        parent::__set($column, $value);    }    public function toString() {        return $this->id;    }}